import {
    Avatar,
    Box,
    Button,
    Card,
    CardHeader,
    FormControl,
    Grid,
    InputLabel,
    MenuItem,
    Pagination,
    Select,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Typography,
  } from "@mui/material";

  import React from "react";
//   import { dressPage1 } from "../../../Data/dress/page1";
  import { useLocation, useNavigate } from "react-router-dom";
  import { useState } from "react";
  import { useEffect } from "react";
  import { useDispatch, useSelector } from "react-redux";
import { deleteProduct, findProducts } from "../../State/Product/Action";
//   import { deleteProduct, findProducts } from "../../../Redux/Customers/Product/Action";

  const ProductsTable = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const { products } = useSelector((store) => store);
    const [filterValue, setFilterValue] = useState({
      availability: "",
      category: "",
      sort: "",
    });

    // query 
    const searchParams = new URLSearchParams(location.search);
    const availability = searchParams.get("availability");
    const category = searchParams.get("category");
    const sort = searchParams.get("sort");
    const page = searchParams.get("page");


    // const handlePaginationChange = (event, value) => {
    //   searchParams.set("page", value-1);
    //   const query = searchParams.toString();
    //   navigate({ search: `?${query}` });
    // };

    useEffect(() => {
      // setFilterValue({ availability, category, sort });
      const data = {
        category:category || "",
        colors: [],
        sizes: [],
        minPrice: 0,
        maxPrice: 1000000,
        minDiscount: 0,
        sort: sort || "price_low",
        pageNumber:page || 1,
        pageSize: 30,
        stock: availability,
      };
      dispatch(findProducts(data));
    },[products.deleteProduct]);
    // }, [availability, category, sort,page,products.deleteProduct]);

    // const handleFilterChange = (e, sectionId) => {
    //   console.log(e.target.value, sectionId);
    //   setFilterValue((values) => ({ ...values, [sectionId]: e.target.value }));
    //   searchParams.set(sectionId, e.target.value);
    //   const query = searchParams.toString();
    //   navigate({ search: `?${query}` });
    // };

    const handleDeleteProduct=(productId)=>{
      console.log("delete product ",productId)
      dispatch(deleteProduct(productId))
    }

    return (
      <Box width={"100%"}>
        {/* <Card className="p-3">
          <CardHeader
            title="Sort"
            sx={{
              pt: 0,
              alignItems: "center",
              "& .MuiCardHeader-action": { mt: 0.6 },
            }}
          />
          <Grid container spacing={2}>
            <Grid item xs={4}>
              <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Category</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={filterValue.category}
                  label="Category"
                //   onChange={(e) => handleFilterChange(e, "category")}
                >
                  <MenuItem value={"pant"}>Men's Pants</MenuItem>
                  <MenuItem value={"mens_kurta"}>Men's Kurta</MenuItem>
                  <MenuItem value={"saree"}>Saree</MenuItem>
                  <MenuItem value={"lengha_choli"}>Lengha Choli</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={4}>
              <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">
                  Availability
                </InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={filterValue.availability}
                  label="Availability"
                //   onChange={(e) => handleFilterChange(e, "availability")}
                >
                  <MenuItem value={"All"}>All</MenuItem>
                  <MenuItem value={"in_stock"}>Instock</MenuItem>
                  <MenuItem value={"out_of_stock"}>Out Of Stock</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={4}>
              <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">
                  Sort By Price
                </InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={filterValue.sort}
                  label="Sort By Price"
                //   onChange={(e) => handleFilterChange(e, "sort")}
                >
                  <MenuItem value={"price_high"}>High - Low</MenuItem>
                  <MenuItem value={"price_low"}>Low - Heigh</MenuItem>
                </Select>
              </FormControl>
            </Grid>
