        <Card className="mt-2">
          <CardHeader
            title="All Products"
            sx={{
              pt: 2,
              alignItems: "center",
              "& .MuiCardHeader-action": { mt: 0.6 },
            }}
          />
          <TableContainer>
            <Table sx={{ minWidth: 800 }} aria-label="table in dashboard">
              <TableHead>
                <TableRow>
                  <TableCell>Image</TableCell>
                  <TableCell>Title</TableCell>
                  <TableCell sx={{ textAlign: "center" }}>Category</TableCell>
                  <TableCell sx={{ textAlign: "center" }}>Price</TableCell>
                  <TableCell sx={{ textAlign: "center" }}>Quantity</TableCell>
                  <TableCell sx={{ textAlign: "center" }}>Delete</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {products?.products?.content?.map((item) => (
                  <TableRow
                    hover
                    key={item.name}
                    sx={{ "&:last-of-type td, &:last-of-type th": { border: 0 } }}

                  >
                    <TableCell>
                      {" "}
                      <Avatar alt={item.title} src={item.imageUrl} />{" "}
                    </TableCell>

                    <TableCell
                      sx={{ py: (theme) => `${theme.spacing(0.5)} !important` }}
                    >
                      <Box sx={{ display: "flex", flexDirection: "column" }}>
                        <Typography
                          sx={{
                            fontWeight: 500,
                            fontSize: "0.875rem !important",
                          }}
                        >
                          {item.title}
                        </Typography>
                        <Typography variant="caption">{item.brand}</Typography>
                      </Box>
                    </TableCell>
                    <TableCell sx={{ textAlign: "center" }}>{item.category.name}</TableCell>
                    <TableCell sx={{ textAlign: "center" }}>{item.discountedPrice}</TableCell>
                    <TableCell sx={{ textAlign: "center" }}>{item.quantity}</TableCell>

                    <TableCell sx={{ textAlign: "center" }}>
                      <Button variant="text" onClick={()=>handleDeleteProduct(item._id)}>Delete</Button> 

                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Card>
        <Card className="mt-2 border">
          {/* <Pagination
            className="py-5 border w-auto"
            size="large"
            count={10}
            color="primary"
            onChange={handlePaginationChange}
          /> */}

          <div className="mx-auto px-4 py-5 flex justify-center shadow-lg rounded-md">
            {/* <Pagination
              count={customersProduct.products?.totalPages}
              color="primary"
              className=""
              onChange={handlePaginationChange}
              // value={page}
            /> */}
          </div>
        </Card>
      </Box>
    );
  };

  export default ProductsTable;
