import React from 'react'
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";
import RemoveCircleOutlineIcon from "@mui/icons-material/RemoveCircleOutline";
import { Button, IconButton } from '@mui/material';
import { useDispatch } from 'react-redux';
import { removeCartItem, updateCartItem } from '../../../State/Cart/Action';

const CartItem = ({item}) => {
    const dispatch=useDispatch();
    const handleUpdateCartItem=(num)=>{
        const data={data:{quantity:item.quantity+num},cartItemId:item?._id}
        dispatch(updateCartItem(data))
    }

    const handleRemoveItemFromCart = () => {
        // const data = { cartItemId: item?._id, jwt };
        dispatch(removeCartItem(item._id));
      };

    return (
        <div className="p-5 shadow-lg border rounded-md">
            <div className="flex items-center">
                <div className="w-[5rem] h-[5rem] lg:w-[9rem] lg:h-[9rem] ">
                    <img
                        className="w-full h-full object-cover object-top"
                        src={item.product?.imageUrl}
                        alt=""
                    />
                </div>
                <div className="ml-5 space-y-1">
                    <p className="font-bold">{item.product.title}</p>
                    <p className="opacity-70">Size:{item.size},White</p>
                    <p className="opacity-70 mt-2">Seller: {item.product.brand}</p>
                    <div className="flex space-x-2 items-center pt-6">
                        <p className="opacity-50 line-through">${item.price}</p>
                        <p className="font-semibold text-lg">
                            ${item.discountedPrice}
                        </p>
                        <p className="text-green-600 font-semibold">
                        {item.product.discountPersent}% off
                        </p>
                    </div>
                </div>
            </div>
            <div className="lg:Flex items-center lg:space-x-10 pt-4">
                <div className="flex items-center space-x-2 ">
                    <IconButton sx={{ color: "RGB(145 85 253)" }} onClick={()=>handleUpdateCartItem(-1)} disabled={item.quantity<=1} color="primary" aria-label="add an alarm">
                        <RemoveCircleOutlineIcon />
                    </IconButton>

                    <span className="py-1 px-7 border rounded-sm">{item.quantity}</span>
                    <IconButton sx={{ color: "RGB(145 85 253)" }} onClick={()=>handleUpdateCartItem(1)} color="primary" aria-label="add an alarm">
                        <AddCircleOutlineIcon />
                    </IconButton>
                    <div>

                    <Button onClick={handleRemoveItemFromCart} sx={{ color: "RGB(145 85 253)" }}>
                        Remove
                    </Button>

                    </div>
                </div>

            </div>
        </div>
    )
}

export default CartItem
