import InboxIcon from "@mui/icons-material/MoveToInbox";
import MailIcon from "@mui/icons-material/Mail";
import DashboardIcon from "@mui/icons-material/Dashboard";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import { Box, CssBaseline, Drawer, List, ListItem, ListItemButton, ListItemIcon, ListItemText, Toolbar, useMediaQuery, useTheme } from '@mui/material';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';


const menu=[
{name:"Dashboard",path:"/admin", icon: <DashboardIcon/>},
{name:"Products",path:"/admin/products",icon: <DashboardIcon/>},
{name:"Customers",path:"/admin/customers",icon: <DashboardIcon/>},
{name:"Orders",path:"/admin/orders",icon: <DashboardIcon/>},
{name:"AddProduct",path:"/admin/product/create",icon: <DashboardIcon/>},
// {name:"",path:""},

]
const Admin=()=> {
    const theme = useTheme();
    const isLargeScreen = useMediaQuery(theme.breakpoints.up("lg"));
    const [sideBarVisible, setSideBarVisible]= useState(false)
    const navigate = useNavigate();

    const drawer = (
        <Box
          sx={{
            overflow: "auto",
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
            height:"100%"
          }}
        >
          {/* {isLargeScreen && <Toolbar />} */}
          <List>
            {menu.map((item, index) => (
              <ListItem key={item.name} disablePadding onClick={()=>navigate(item.path)}>
                <ListItemButton>
                  <ListItemIcon>
                    {item.icon}
                  </ListItemIcon>
                  <ListItemText >{item.name}</ListItemText>
                </ListItemButton>
              </ListItem>
            ))}
          </List>

          <List>

              <ListItem  disablePadding>
                <ListItemButton>
                  <ListItemIcon>
                    <AccountCircleIcon/>
                  </ListItemIcon>
                  <ListItemText >Account</ListItemText>
                </ListItemButton>
              </ListItem>

          </List>

          {/* <List sx={{ position: "absolute", bottom: 0, width: "100%" }}>
            <Divider />

            <ListItem onClick={handleLogout}  disablePadding >
                <ListItemButton>
                <Avatar
                            className="text-white"
                            onClick={handleLogout}

                            sx={{
                              bgcolor: deepPurple[500],
                              color: "white",
                              cursor: "pointer",
                            }}
                          >
                            {auth.user?.firstName[0].toUpperCase()}
                          </Avatar>
                  <ListItemText className="ml-5" primary={"Logout"} />
                </ListItemButton>
              </ListItem>

          </List> */}


        </Box>
      );


    return (
        <div>
      <Box sx={{ display: `${isLargeScreen ? "flex" : "block"}` }}>
        <CssBaseline />
        {/* <AdminNavbar handleSideBarViewInMobile={handleSideBarViewInMobile} /> */}

        <Drawer
          variant='permanent'
          sx={{
            height:"100vh",
            width: 240,
            flexShrink: 0,
            // [`& .MuiDrawer-paper`]: {
            //   width: drawerWidth,
            //   boxSizing: "border-box",
            //   ...(drawerVariant === "temporary" && {
            //     top: 0,
            //     [`& .MuiPaper-root.MuiDrawer-paperAnchorTop.MuiDrawer-paperTemporary`]:
            //       {
            //         position: "fixed",
            //         left: 0,
            //         right: 0,
            //         height: "100%",
            //         zIndex: (theme) => theme.zIndex.drawer + 2,
            //       },
            //   }),
            // },
          }}
        //   open={isLargeScreen || sideBarVisible}
        //   onClose={handleCloseSideBar}
        >
          {drawer}
        </Drawer>
        {/* <Box className="adminContainer" component="main" sx={{ flexGrow: 1 }}>
          <Toolbar />
          <Routes>
            <Route path="/" element={ <Dashboard />}></Route>
            <Route path="/product/create" element={<CreateProductForm/>}></Route>
            <Route path="/product/update/:productId" element={<UpdateProductForm/>}></Route>
            <Route path="/products" element={<ProductsTable/>}></Route>
            <Route path="/orders" element={<OrdersTable/>}></Route>
            <Route path="/customers" element={<Customers/>}></Route>
            <Route path="/demo" element={<DemoAdmin />}></Route>
          </Routes>

        </Box> */}
      </Box>
        </div>
    )
}

export default Admin
